<?phpfunction text_get(){	//return file_get_contents('data/text.txt');}function text_set($text){	file_put_contents('data/text.txt', $text);}function startup(){$host='localhost';    $login='root';    $password='';    $db_name='boom';    mysql_connect($host,$login, $password) or die ('We can\'t access to DB');    mysql_select_db($db_name)  or die ('We can\'t access to table');}function incert_user($user){    $login=$user[0];    $password=md5($user[1]);    $zapros="INSERT INTO users (login, password) VALUES ('$login', '$password')";    $res=mysql_query($zapros);    mysql_close();    return $res;}function select_user($user){    $login=$user[0];    $password=md5($user[1]);    $zapros="SELECT * FROM users WHERE login='$login' AND password='$password'";    $res=mysql_query($zapros);    while ($row = mysql_fetch_array($res)) {        return $row[0] ;    }    mysql_close();    return null;}function select_id_author($name){    $zapros = "SELECT id FROM authors WHERE name_autor='$name'";    $res = mysql_query($zapros);    while ($row = mysql_fetch_array($res)) {        return $row[0] ;    }    mysql_close();    return null;}function select_name_author($id){    $zapros = "SELECT name_autor FROM authors WHERE id='$id'";    $res = mysql_query($zapros);    while ($row = mysql_fetch_array($res)) {        return $row[0] ;    }    mysql_close();    return null;}function select_id_photo($path){    $zapros = "SELECT id FROM photos WHERE path='$path'";    $res = mysql_query($zapros);    while ($row = mysql_fetch_array($res)) {        return $row[0] ;    }    return null;}function select_path_photo($id){    try {        $zapros = "SELECT pach FROM photos WHERE id='$id'";        $res = mysql_query($zapros);        while ($row = mysql_fetch_array($res)) {            return $row[0];        }        return null;    } catch (Exception $e) {        return null;    }}function select_name_articles(){    startup();    $zapros = "SELECT id, name_article FROM article LIMIT 3";    $res = mysql_query($zapros);    $gazeta=array();    while ($statia = mysql_fetch_array($res)) {        $gazeta[$statia[0]]=$statia[1] ;    }    return  $gazeta;}function select_all_article($id){    startup();    $zapros = "SELECT * FROM article WHERE id='$id'";    $res = mysql_query($zapros);    $gazeta = array();    return $gazeta=mysql_fetch_assoc($res);}function incert_photo($path){    connect_data_base();    $zapros="INSERT INTO photos (path) VALUES ('$path')";    $res=mysql_query($zapros);    mysql_close();    return $res;}function incert_autor($name){    $zapros="INSERT INTO authors (name_autor) VALUES ('$name')";    $res=mysql_query($zapros);    mysql_close();    return $res;}function incert_article($article){    $data_s=date("Y-m-d");    $id_photo=1;    $article[1]=mysql_real_escape_string( $article[1]);    $zapros="INSERT INTO article (name_article, text_article, date_create, date_update, id_author, id_photo) VALUES ('$article[0]', '$article[1]', '$data_s', '$data_s', '$article[2]','$id_photo')";    $res=mysql_query($zapros);    mysql_close();    return $res;}function update_article($article){    $date_u=date("Y-m-d");    $article[2]=mysql_real_escape_string( $article[2]);    $zapros="UPDATE article SET  name_article = '$article[1]', text_article='$article[2]', date_update='$date_u', id_author='$article[3]' WHERE id='$article[0]'";    $res=mysql_query($zapros);    mysql_close();    return $res;}function  delete_article($id){    $zapros="DELETE FROM article WHERE id='$id'";    $res=mysql_query($zapros);    mysql_close();    return $res;}?>